changeSet.sql:
DELETE FROM Player;
DELETE FROM ShipGood;
DELETE FROM Ship;
DELETE FROM Race;
DELETE FROM SystemGood;
DELETE FROM SystemShip;
DELETE FROM System;
DELETE FROM Good;
DELETE FROM BaseShip;
DELETE FROM Shield;
DELETE FROM Weapon;
DELETE FROM JumpDrive;

/* Migrate Player credits */
UPDATE Ship SET Credits = (SELECT CashCredits FROM Player p WHERE p.ShipId = Ship.ShipId)
WHERE EXISTS (SELECT 1 FROM Player p WHERE p.ShipId = Ship.ShipId);
/* Migrate NPC credits */
UPDATE Ship SET Credits = (SELECT Credits FROM Npc n WHERE n.ShipId = Ship.ShipId)
WHERE EXISTS (SELECT 1 FROM Npc n WHERE n.ShipId = Ship.ShipId);

      INSERT INTO SystemJumpDriveUpgrade (SystemId, JumpDriveId, Quantity, PriceMultiplier)
      SELECT SystemId, JumpDriveId, 5 AS 'Quantity', 1.0 AS 'PriceMultiplier' FROM JumpDrive, System;

      INSERT INTO SystemWeaponUpgrade (SystemId, WeaponId, Quantity, PriceMultiplier)
      SELECT SystemId, WeaponId, 5 AS 'Quantity', 1.0 AS 'PriceMultiplier' FROM Weapon, System;

      INSERT INTO SystemShieldUpgrade (SystemId, ShieldId, Quantity, PriceMultiplier)
      SELECT SystemId, ShieldId, 5 AS 'Quantity', 1.0 AS 'PriceMultiplier' FROM Shield, System;
